import React, {useState} from "react";
import {Route, Routes, Navigate} from "react-router-dom";

import Sidebar from "./components/Sidebar/Sidebar";
import ModalCreating from "./components/ModalCreating/ModalCreating";
import Main from "./pages/Main";
import Products from "./pages/Products";
import Sales from "./pages/Sales";
import Cabinet from "./pages/Cabinet";
import Logout from "./pages/Logout";

import './assets/_global.scss'

function App() {

  const [modal, setModal] = useState(false);
  return (
    <div className="App">
      <Sidebar />
        <Routes>
          <Route path="/" element={<Main setModal={setModal} />} />
          <Route path="/products" element={<Products setModal={setModal} />} />
          <Route path="/sales" element={<Sales setModal={setModal} />} />
          <Route path="/cabinet" element={<Cabinet setModal={setModal} />} />
          <Route path="/cabinet" element={<Logout setModal={setModal} />} />
          <Route
            path="*"
            element={<Navigate to="/" />}
          />
        </Routes>
      {modal
        ? <ModalCreating setModal={setModal} />
        : null
      }

    </div>
  );
}

export default App;
