import React, {useState} from 'react';
import {useNavigate} from "react-router-dom";

const CreateAccount = () => {
	const navigate = useNavigate()

	const [firstName, setFirstName] = useState('First name');
	const [lastName, setLastName] = useState('Last name');
	const [companyName, setCompanyName] = useState('Company name');
	const [category, setCategory] = useState('Product Category');
	const [quantity, setQuantity] = useState('Quantity of goods');
	const [weight, setWeight] = useState('Weight / Volume of one item');

	const saveUser = () => {
		const user = {
			name: 'Ivan',
			email: 'test@test.ru',
			password: 'qwerty123456'
		}
		localStorage.setItem('User', JSON.stringify(user))
		navigate('/signin')
	}

	return (
		<div className="wrapper">
			<div className="l-content">
				<div className="form">
					<h1>Create an account</h1>
					<div className="first">
						<div className="row">
							<label>First name</label>
							<input type="text" placeholder={firstName} id="firstName" onChange={(e) => setFirstName(e.target.firstName)} />
						</div>
						<div className="row">
							<label>Last name</label>
							<input type="text" placeholder="Last name" id="lastName" onChange={(e) => setLastName(e.target.lastName)} />
						</div>
					</div>
					<div className="row">
						<label>Company name</label>
						<input type="text" placeholder="Company name" id="companyName" onChange={(e) => setCompanyName(e.target.companyName)} />
					</div>
					<div className="row">
						<label>Email</label>
						<input type="text" placeholder="Email" id="email" />
					</div>
					<div className="row">
						<label>Password</label>
						<input type="text" placeholder="Enter password" id="password" />
					</div>
					<div className="row">
						<label>Repeat password</label>
						<input type="text" placeholder="Repeat password" id="checkPassword" />
					</div>
					<input type="submit" value="Create account" className="btn" onClick={saveUser}/>
					<div class="text">Already have an account? <a href="/signin" className="link">Log in</a></div>
				</div>
			</div>
			<div className="r-content">
			</div>
		</div>
	);
};

export default CreateAccount;
