import React, {useState} from 'react';

import Title from "../Title/Title";

import './style.scss'

import add from '../../assets/images/icons/add.svg';
import Modal from "../Modal/Modal";

const Header = (props) => {

	const [modal, setModal] = useState(false);

	const [list, setList] = useState([
		{
			value: 'Store'
		},
		{
			value: 'Pirce'
		},
		{
			value: 'Product name'
		},
		{
			value: 'Product Category'
		},
		{
			value: 'Quantity of goods'
		},
		{
			value: 'Weight / Volume of one item'
		}
	])

	return (
		<div className="header">
			<Title
				title={props.title}
				subtitle={props.subtitle}
			/>
			<div className="btn" onClick={() => setModal(true)}>
				<img src={add} alt="Create a product"/>
				Create a product
			</div>

			{
				modal
				? <Modal setModal={setModal}>
						<h2>Creating a product</h2>
						{list.map((item) => {
							return <input type="text" placeholder={item.value} />
						})}
						<input type="submit" value='Add Product' className="btn addBtn" />
					</Modal>
				:null
			}


		</div>
	);
};

export default Header;
