import React, {useState} from 'react';

import Title from "../Title/Title";

import './style.scss'

import add from '../../assets/images/icons/add.svg';
import Modal from "../Modal/Modal";

const Header = (props) => {

	const [modal, setModal] = useState(false);

	const [store, setStore] = useState('');
	const [storeError, setStoreError] = useState(false);
	const [pirce, setPirce] = useState('');
	const [pirceError, setPirceError] = useState(false)
	const [name, setName] = useState('');
	const [nameError, setNameError] = useState(false)
	const [category, setCategory] = useState('');
	const [categoryError, setCategoryError] = useState(false);
	const [quantity, setQuantity] = useState('');
	const [quantityError, setQuantityError] = useState(false);
	const [weight, setWeight] = useState('');
	const [weightError, setWeightError] = useState(false);

	function validateStore() {
		if (Store.length < 3) {
			setFnError(true)
			return false
		} else {
			setFnError(false)
			return true
		}
	}


	return (
		<div className="header">
			<Title
				title={props.title}
				subtitle={props.subtitle}
			/>
			<div className="btn" onClick={() => setModal(true)}>
				<img src={add} alt="Create a product"/>
				Create a product
			</div>

			{
				modal
				? <Modal setModal={setModal}>
						<h2>Creating a product</h2>
						<input type="text" placeholder="Store" onChange={(e) => setStore(e.target.value)} />
						<input type="text" placeholder="Pirce" onChange={(e) => setPirce(e.target.value)} />
						<input type="text" placeholder="Product name" onChange={(e) => setName(e.target.value)} />
						<input type="text" placeholder="Product Category" onChange={(e) => setCategory(e.target.value)} />
						<input type="text" placeholder="Quantity of goods" onChange={(e) => setQuantity(e.target.value)} />
						<input type="text" placeholder="Weight / Volume of one item" onChange={(e) => setWeight(e.target.value)} />
						<input type="submit" value='Add Product' className="btn addBtn" />
					</Modal>
				:null
			}


		</div>
	);
};

export default Header;
