import React, {useState} from 'react';

import Title from "../Title/Title";

import './style.scss'

import add from '../../assets/images/icons/add.svg';
import Modal from "../Modal/Modal";

const Header = (props) => {

	const [modal, setModal] = useState(false);

	const [store, setStore] = useState('');
	const [storeError, setStoreError] = useState(false);
	const [pirce, setPirce] = useState('');
	const [pirceError, setPirceError] = useState(false)
	const [name, setName] = useState('');
	const [nameError, setNameError] = useState(false)
	const [category, setCategory] = useState('');
	const [categoryError, setCategoryError] = useState(false);
	const [quantity, setQuantity] = useState('');
	const [quantityError, setQuantityError] = useState(false);
	const [weight, setWeight] = useState('');
	const [weightError, setWeightError] = useState(false);

	function validateStore() {
		if (store === "") {
			setStoreError(true)
			return false
		} else {
			setStoreError(false)
			return true
		}
	}

	function validatePirce() {
		if (pirce === "") {
			setPirceError(true)
			return false
		} else {
			setPirceError(false)
			return true
		}
	}

	function validateName() {
		if (name === "") {
			setNameError(true)
			return false
		} else {
			setNameError(false)
			return true
		}
	}

	function validateCategory() {
		if (category === "") {
			setCategoryError(true)
			return false
		} else {
			setCategoryError(false)
			return true
		}
	}

	function validateQuantity() {
		if (quantity === "") {
			setQuantityError(true)
			return false
		} else {
			setQuantityError(false)
			return true
		}
	}

	function validateWeight() {
		if (weight === "") {
			setWeightError(true)
			return false
		} else {
			setWeightError(false)
			return true
		}
	}

	const product = {
		store,
		pirce,
		name,
		category,
		quantity,
		weight
	}

	const saveProduct = async () => {
		validateStore();
		validatePirce();
		validateName();
		validateCategory();
		validateQuantity();
		validateWeight();


		localStorage.setItem('isProd', true);
		localStorage.setItem('Product', JSON.stringify(product));

		if (validateStore() && validatePirce() && validateName() && validateCategory() && validateQuantity() && validateWeight()){
			setModal(false)
		} else{
			console.log(111)
		}

	}

	const handleStoreChange = (e) => {
		setStoreError(false)
		setStore(e.target.value)
	}

	const handlePirceChange = (e) => {
		setPirceError(false)
		setPirce(e.target.value)
	}

	const handleNameChange = (e) => {
		setNameError(false)
		setName(e.target.value)
	}

	const handleCategoryChange = (e) => {
		setCategoryError(false)
		setCategory(e.target.value)
	}

	const handleQuantityChange = (e) => {
		setQuantityError(false)
		setQuantity(e.target.value)
	}

	const handleWeightChange = (e) => {
		setWeightError(false)
		setWeight(e.target.value)
	}

	const isProd = localStorage?.getItem('isProd') === 'true'

	return (
		<div className="header">
			{isProd}
			<Title
				title={props.title}
				subtitle={props.subtitle}
			/>
			<div className="btn" onClick={() => setModal(true)}>
				<img src={add} alt="Create a product"/>
				Create a product
			</div>

			{
				modal
				? <Modal setModal={setModal}>
						<h2>Creating a product</h2>
						<input
							className={storeError ? 'red' : '' }
							type="text"
							placeholder="Store"
							onChange={handleStoreChange}
						/>
						<input
							className={pirceError ? 'red' : '' }
							type="text"
							placeholder="Pirce"
							onChange={handlePirceChange}
						/>
						<input
							className={nameError ? 'red' : '' }
							type="text"
							placeholder="Product name"
							onChange={handleNameChange}
						/>
						<input
							className={categoryError ? 'red' : '' }
							type="text"
							placeholder="Product Category"
							onChange={handleCategoryChange}
						/>
						<input
							className={quantityError ? 'red' : '' }
							type="text"
							placeholder="Quantity of goods"
							onChange={handleQuantityChange}
						/>
						<input
							className={weightError ? 'red' : '' }
							type="text"
							placeholder="Weight / Volume of one item"
							onChange={handleWeightChange}
						/>
						<input type="submit" value='Add Product' className="btn addBtn" onClick={saveProduct}/>
					</Modal>
				:null
			}


		</div>
	);
};

export default Header;
