import React, {useState} from 'react';
import {Link, useNavigate} from "react-router-dom";

import './style.scss'

const Sign = () => {

	const navigate = useNavigate();

	const [email2, setEmail2] = useState('');
	const [password2, setPassword2] = useState('');

	const login = () => {

		// код валидации и проверки по обьекту с локалстораджа
		const user = localStorage.getItem('User');
		console.log(JSON.parse(user));

		const mail = JSON.parse(user).email
		console.log(mail);
		const pass = JSON.parse(user).password
		console.log(pass);

		function validateEmail2(){
			if(mail===email2){
				return true
			}
		}

		if( mail===email2 && pass===password2){
			console.log('Успешно')
			navigate('/main')
		} else{
			console.error('Ошибка')
		}


		localStorage.setItem('isAuth', true)

		// navigate на главную страницу

	}
	return (
		<div className="wrapper">
			<div className="l-content">
				<div className="form">
					<h1>Sign in</h1>
					<div className="row">
						<label>Email</label>
						<input type="text" placeholder="Email" onChange={(e) => setEmail2(e.target.value)} />
					</div>
					<div className="row">
						<label>Password</label>
						<input type="password" placeholder="Enter password" onChange={(e)=> setPassword2(e.target.value)} />
					</div>
					<input type="submit" value="Log in" className="btn" onClick={login}/>
					<Link to="/" className="link">Forgot password?</Link>
				</div>
			</div>
			<div className="r-content">
			</div>
		</div>
	);
};

export default Sign;
