import React from 'react';

const Modal = ({ children, setModal, modalTitle, btnTitle }) => {
	return (
		<div>
			{children}
			<div className="modal">
				<div className="modal-content">
					<h2>{modalTitle}</h2>
					<div className="close" onClick={() => setModal(false)}></div>
					<input type="text" placeholder="Number of products" />
					<input type="text" placeholder="Date of sale"/>
					<input type="submit" value={btnTitle} className="btn addBtn" />
				</div>
			</div>
		</div>
	);
};

export default Modal;
