import React, {useRef, useState} from 'react';
import {useNavigate} from "react-router-dom";

import './style.scss'

const CreateAcc = () => {
	const navigate = useNavigate()

	const [firstName, setFirstName] = useState('');
	const [fnError, setFnError] = useState(false)
	const fnRef = useRef()
	const [lastName, setLastName] = useState('');
	const [lnError, setLnError] = useState(false)
	const lnRef = useRef()
	const [companyName, setCompanyName] = useState('');
	const [cnError, setCnError] = useState(false)
	const cnRef = useRef()
	const [email, setEmail] = useState('');
	const [emailError, setEmailError] = useState(false)
	const emailRef = useRef()
	const [password, setPassword] = useState('');
	const [passwordError, setPasswordError] = useState(false);
	const passwordRef = useRef()
	const [checkPassword, setCheckPassword] = useState('');
	const [checkPasswordError, setCheckPasswordError] = useState(false)
	const checkPasswordRef = useRef()

	function validateFirstName() {
		console.log('===>firstName.length', firstName.length);
		if (firstName.length < 3){
			console.log('===>Not validate');
			setFnError(true)
			return false
		} else {
			console.log('===>Validate');
			setFnError(false)
			return true
		}
	}

	function validateLastName() {
		console.log('===>lastName.length', lastName.length);
		if (lastName.length < 3){
			console.log('===>Not validate');
			setLnError(true)
			return false
		} else {
			console.log('===>Validate');
			setLnError(false)
			return true
		}
	}

	function validateCompanyName() {
		console.log('===>companyName.length', companyName.length);
		if (companyName.length < 3){
			console.log('===>Not validate');
			setCnError(true)
			return false
		} else {
			console.log('===>Validate');
			setCnError(false)
			return true
		}
	}

	function validateCompanyName() {
		console.log('===>companyName.length', companyName.length);
		if (companyName.length < 3){
			console.log('===>Not validate');
			setCnError(true)
			return false
		} else {
			console.log('===>Validate');
			setCnError(false)
			return true
		}
	}

	function validateEmail() {
		console.log('===>Email.length', companyName.length);
		let regex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
		if (regex.test(email) && email != ""){
			console.log('===>Validate');
			setEmailError(false)
			return false
		} else {
			console.log('===>Not Validate');
			setEmailError(true)
			return true
		}
	}

	function validatePassword() {
		console.log('===>Email.length', companyName.length);
		let regex = /^(?=.*\d)(?=.*[A-Z]).{6,}$/;
		if (regex.test(password) && password != ""){
			console.log('===>Validate');
			setPasswordError(false)
			return false
		} else {
			console.log('===>Not Validate');
			setPasswordError(true)
			return true
		}
	}



	function validateCheckPassword() {
		if (password === checkPassword && password !== '') {
			setCheckPasswordError(false)
			return true
		} else {
			setCheckPasswordError(true)
			return false
		}
	}


	const saveUser = async () => {
		const user = {
			firstName,
			lastName,
			companyName,
			email,
			password,
			checkPassword
		}
		validateFirstName();
		validateLastName();
		validateCompanyName();
		validateEmail();
		validatePassword();
		validateCheckPassword();

		console.log('===>fnRef.current', fnRef.current.classList.contains('red')  );

		if(fnError && lnError && cnError && emailError && passwordError && checkPasswordError){
			console.error('Ошибка')
		} else{
			// console.log("Удачно")
			// localStorage.setItem('User', JSON.stringify(user))
			// navigate('/signin')
		}
	}

	return (
		<div className="wrapper">
			<div className="l-content">
				<div className="form">
					<h1>Create an account</h1>
					<div className="first">
						<div className="row">
							<label>First name</label>
							<input
								ref={fnRef}
								className={fnError ? 'red' : ''}
								type="text"
								placeholder="First name"
								id="firstName"
								onChange={(e) => setFirstName(e.target.value)}
							/>
						</div>
						<div className="row">
							<label>Last name</label>
							<input ref={lnRef} className={lnError ? 'red' : ''} type="text" placeholder="Last name" id="lastName" onChange={(e) => setLastName(e.target.value)} />
						</div>
					</div>
					<div className="row">
						<label>Company name</label>
						<input ref={cnRef} className={cnError ? 'red' : ''} type="text" placeholder="Company name" id="companyName" onChange={(e) => setCompanyName(e.target.value)} />
					</div>
					<div className="row">
						<label>Email</label>
						<input ref={emailRef} className={emailError ? 'red' : ''} type="text" placeholder="Email" id="email" onChange={(e) => setEmail(e.target.value)} />
					</div>
					<div className="row">
						<label>Password</label>
						<input ref={passwordRef} className={passwordError ? 'red' : ''} type="password" placeholder="Enter password" id="password" onChange={(e) => setPassword(e.target.value)} />
					</div>
					<div className="row">
						<label>Repeat password</label>
						<input ref={checkPasswordRef} className={checkPasswordError ? 'red' : ''} type="password" placeholder="Repeat password" id="checkPassword" onChange={(e) => setCheckPassword(e.target.value)} />
					</div>
					<input type="submit" value="Create account" className="btn" onClick={saveUser}/>
					<div className="text">Already have an account? <a href="/signin" className="link">Log in</a></div>
				</div>
			</div>
			<div className="r-content">
			</div>
		</div>
	);
};

export default CreateAcc;
