import React, {useState} from 'react';

import Title from "../Title/Title";

import './style.scss'

import add from '../../assets/images/icons/add.svg';
import Modal from "../Modal/Modal";

const Header = (props) => {

	const [modal, setModal] = useState(false);

	const [store, setStore] = useState('Store');
	const [pirce, setPirce] = useState('Pirce');
	const [name, setName] = useState('Product name');
	const [category, setCategory] = useState('Product Category');
	const [quantity, setQuantity] = useState('Quantity of goods');
	const [weight, setWeight] = useState('Weight / Volume of one item');

	return (
		<div className="header">
			<Title
				title={props.title}
				subtitle={props.subtitle}
			/>
			<div className="btn" onClick={() => setModal(true)}>
				<img src={add} alt="Create a product"/>
				Create a product
			</div>

			{
				modal
				? <Modal setModal={setModal}>
						<h2>Creating a product</h2>
						<input type="text" placeholder={store} onChange={(e) => setStore(e.target.store)} />
						<input type="text" placeholder={pirce} onChange={(e) => setPirce(e.target.pirce)} />
						<input type="text" placeholder={name} onChange={(e) => setName(e.target.name)} />
						<input type="text" placeholder={category} onChange={(e) => setCategory(e.target.category)} />
						<input type="text" placeholder={quantity} onChange={(e) => setQuantity(e.target.quantity)} />
						<input type="text" placeholder={weight} onChange={(e) => setWeight(e.target.weight)} />
						<input type="submit" value='Add Product' className="btn addBtn" />
					</Modal>
				:null
			}


		</div>
	);
};

export default Header;
